(defn optimize-move [board]   
  (:direction (last (sort-by #(:score %) [
    (eval-option board cost-fn :up)
    (eval-option board cost-fn :down)
    (eval-option board cost-fn :left)
    (eval-option board cost-fn :right)]))))


(defn options-move [board]   
  (sort-by #(:score %) [
    (eval-option board cost-fn :up)
    (eval-option board cost-fn :down)
    (eval-option board cost-fn :left)
    (eval-option board cost-fn :right)]))

