(defn fmap 
  [f m]
  (into () (for [[k v] m] (f k v))))

(fmap (fn [k v] {v k}) {1 2 3 4}  )

;
; TODO optimize to put in bucket and the select among the moves in the bucket
(defn optimal-minimax-move [board]
    (let [scoring (minimax (build 4 board))] 
      (let [t (fmap (fn [k v] (hash-map :min (:min v) :mean (:mean v) :max (:max v) :direction k )) scoring)]
        (:direction (last (sort-by #(+ (* 16 (:mean %)) (:mean %)) t)))
)))

(defn optimal-minimax-move [board]
    (let [scoring (minimax (build 3 board))] 
      (let [t (fmap (fn [k v] (hash-map :min (:min v) :mean (:mean v) :max (:max v) :direction k )) scoring)]
        (:direction (last (sort-by #(:max %) t)))
)))


